mapper.sql:
select * from task_getdata_taobao a where a.status!=3

     and a.type=#{type}


    and a.status=#{status}


    and a.task_name like #{taskName}

select * from task_getdata_taobao_order a where 1=1

    and a.orderinfo_createtime >= #{beginDate}


    and a.orderinfo_createtime <= #{endDate}


    and a.orderinfo_id like #{orderinfoId}


    and a.order_bar_text like #{orderBarText}


    and a.statusinfo_text like #{statusinfoText}


    and a.logistics_name like #{logisticsName}


    and a.logistics_num like #{logisticsNum}


    and (a.buyer_nick like #{buyerNick}
    or a.buyer_phonenum like #{buyerNick}
    or a.buyer_logistics_phone like #{buyerNick}
    )


    and  a.buyer_address like #{buyerAddress}

select  a.task_id, a.buyer_id, a.buyer_nick, a.buyer_phonenum, a.buyer_provice, a.buyer_city, a.buyer_area,
a.buyer_post, a.buyer_address, a.buyer_logistics_phone, a.orderinfo_createtime, a.orderinfo_id,
a.payinfo_actualfee, a.statusinfo_text, a.statusinfo_type, a.create_time, a.pay_time, a.send_time,
a.alipay_id, a.order_bar, a.order_bar_text, a.logistics_name, a.logistics_num, a.logistics_num_taobao,
a.logistics_last_desc, a.buy_message, a.created_by, a.updated_by, a.created_at, a.updated_at,
b.id   from task_getdata_taobao_order a ,task_getdata_taobao_orderids b
where a.orderinfo_id=b.orderinfo_id

    and a.orderinfo_id like #{orderinfoId}


    and a.order_bar_text like #{orderBarText}


    and a.statusinfo_text like #{statusinfoText}


    and a.logistics_name like #{logisticsName}


    and a.logistics_num like #{logisticsNum}


    and (a.buyer_nick like #{buyerNick}
    or a.buyer_phonenum like #{buyerNick}
    or a.buyer_logistics_phone like #{buyerNick}
    )


    and  a.buyer_address like #{buyerAddress}

and b.task_id=#{id}
