mapper.sql:
select * from task_getdata_taobao a where a.status!=3

     and a.type=#{type}


    and a.status=#{status}


    and a.task_name like #{taskName}

select * from task_getdata_taobao a where 1=1

    and a.orderinfo_createtime >= #{beginDate}


    and a.orderinfo_createtime <= #{endDate}


    and a.orderinfo_id like #{orderinfoId}


    and a.order_bar_text like #{orderBarText}


    and a.statusinfo_text like #{statusinfoText}


    and a.logistics_name like #{logisticsName}


    and a.logistics_num like #{logisticsNum}


    and (a.buyer_nick like #{buyerNick}
    or a.buyer_phonenum like #{buyerNick}
    or a.buyer_logistics_phone like #{buyerNick}
    )


    and (a.buyer_provice like #{buyerArea}
    or a.buyer_city like #{buyerArea}
    or a.buyer_area like #{buyerArea}
    or a.buyer_post like #{buyerArea}
    )

select a.* from task_getdata_taobao_order a ,task_getdata_taobao_orderids b
where a.orderinfo_id=b.orderinfo_id

    and a.orderinfo_id like #{orderinfoId}


    and a.order_bar_text like #{orderBarText}


    and a.statusinfo_text like #{statusinfoText}


    and a.logistics_name like #{logisticsName}


    and a.logistics_num like #{logisticsNum}


    and (a.buyer_nick like #{buyerNick}
    or a.buyer_phonenum like #{buyerNick}
    or a.buyer_logistics_phone like #{buyerNick}
    )


    and (a.buyer_provice like #{buyerArea}
    or a.buyer_city like #{buyerArea}
    or a.buyer_area like #{buyerArea}
    or a.buyer_post like #{buyerArea}
    )



and b.task_id=#{id}
